import*as THREE from"three";import{commons}from"./lib/Common.js";export class MySphere{constructor(o){this.myAmmoPhysicsWorld=o}create(o=!0,s={x:0,y:50,z:0},m=65280,e=10){let t=.2*e,h=new THREE.Mesh(new THREE.SphereGeometry(t,32,32),new THREE.MeshPhongMaterial({color:m}));h.userData.tag="sphere",h.position.set(s.x,s.y,s.z),h.castShadow=!0,h.receiveShadow=!0,h.collisionResponse=o=>{o.material.color.setHex(16777215*Math.random())};let r=commons.createSphereShape(h),i=commons.createAmmoRigidBody(r,h,.7,.8,s,e);this.myAmmoPhysicsWorld.addPhysicsObject(i,h,o,this.myAmmoPhysicsWorld.COLLISION_GROUP_SPHERE,this.myAmmoPhysicsWorld.COLLISION_GROUP_SPHERE|this.myAmmoPhysicsWorld.COLLISION_GROUP_PLANE|this.myAmmoPhysicsWorld.COLLISION_GROUP_COMPOUND|this.myAmmoPhysicsWorld.COLLISION_GROUP_CONVEX|this.myAmmoPhysicsWorld.COLLISION_GROUP_MOVEABLE|this.myAmmoPhysicsWorld.COLLISION_GROUP_TRIANGLE|this.myAmmoPhysicsWorld.COLLISION_GROUP_BOX|this.myAmmoPhysicsWorld.COLLISION_GROUP_HINGE_SPHERE)}createRandom(o=!0,s=200,m=50){let e={x:-s/2+Math.random()*s,y:m,z:-s/2+Math.random()*s},t=2+20*Math.random();this.create(o,e,16712965,t)}}